import React from "react";
import figmaIcon from "../assets/images/figma.png";
import jsIcon from "../assets/images/js.png";
import reactIcon from "../assets/images/react.png";
import htmlIcon from "../assets/images/html.png";

const skills = [
  {
    icon: figmaIcon,
    name: "Figma",
    desc: "UI Design, Prototyping",
    color: "bg-[#DBEAFE]" // biru lembut
  },
  {
    icon: jsIcon,
    name: "JavaScript",
    desc: "Front-End Development",
    color: "bg-[#FEF9C3]" // kuning lembut
  },
  {
    icon: reactIcon,
    name: "React",
    desc: "Web Apps & SPA",
    color: "bg-[#EDE9FE]" // ungu lembut
  },
  {
    icon: htmlIcon,
    name: "HTML & CSS",
    desc: "Responsive Design",
    color: "bg-[#FCE7F3]" // pink lembut
  },
];

function SkillsSection() {
  return (
    <section id="skills" className="py-20 bg-gray-50">
      <div className="max-w-6xl mx-auto px-6">
        {/* === TITLE === */}
        <div className="text-center mb-12">
          <h2 className="text-3xl md:text-4xl font-bold text-gray-900">
            Keahlian Saya
          </h2>
          <p className="text-gray-600 mt-3 max-w-2xl mx-auto">
            Beberapa teknologi dan alat yang saya gunakan untuk membangun desain
            dan website yang modern serta responsif.
          </p>
        </div>

        {/* === GRID SKILL === */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 md:gap-8">
          {skills.map((skill, index) => (
            <div
              key={index}
              className="bg-white rounded-2xl p-8 flex flex-col items-center shadow-[0_4px_20px_rgba(0,0,0,0.06)] hover:shadow-[0_6px_25px_rgba(0,0,0,0.1)] transition-all duration-300"
            >
              {/* ICON */}
              <div
                className={`w-[64px] h-[64px] ${skill.color} rounded-full flex items-center justify-center mb-4`}
              >
                <img
                  src={skill.icon}
                  alt={skill.name}
                  className="w-[28px] h-[28px] object-contain"
                />
              </div>

              {/* TEXT */}
              <h3 className="text-gray-900 font-semibold text-lg">
                {skill.name}
              </h3>
              <p className="text-gray-500 text-sm mt-1 text-center">
                {skill.desc}
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default SkillsSection;
