import React from "react";
import profile1 from "../assets/images/profile.jpg";
import profile2 from "../assets/images/profile.jpg";
import profile3 from "../assets/images/profile.jpg";

// Data Testimoni
const testimonials = [
  {
    id: 1,
    name: "Citra Nadya",
    role: "Product Manager, Fintech",
    text: "Arie mampu memahami kebutuhan tim kami dan menerjemahkannya menjadi desain yang sangat user-friendly. Komunikasi dan delivery selalu on time!",
    image: profile1,
  },
  {
    id: 2,
    name: "Rama Putra",
    role: "CEO, EduStartup",
    text: "Kreativitas & kecepatan kerja Arie luar biasa. Landing page yang dibuat sangat modern & efektif meningkatkan konversi produk kami.",
    image: profile2,
  },
  {
    id: 3,
    name: "Rizky Pradana",
    role: "Founder, DigitalHive",
    text: "Kerja sama dengan Arie sangat profesional dan menyenangkan. Desain yang dihasilkan selalu berkualitas tinggi dan sesuai harapan klien.",
    image: profile3,
  },
];

function TestimonialSection() {
  return (
    <section id="testimonial" className="py-20 bg-gray-50 flex justify-center">
      <div className="max-w-6xl w-full px-6">
        {/* === TITLE === */}
        <div className="text-center mb-12">
          <h2 className="text-3xl md:text-4xl font-bold text-gray-800">
            Testimoni Klien
          </h2>
          <p className="mt-3 text-gray-600 max-w-2xl mx-auto">
            Pendapat klien yang telah bekerja sama dengan saya dalam berbagai
            proyek desain dan pengembangan web.
          </p>
        </div>

        {/* === GRID CARD === */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-10 justify-items-center">
          {testimonials.map((item) => (
            <div
              key={item.id}
              className="bg-white w-[472px] h-[244px] rounded-[16px]
                         shadow-[0_8px_25px_rgba(0,0,0,0.05)] hover:shadow-[0_12px_35px_rgba(0,0,0,0.08)]
                         transition-all duration-300 border border-gray-100 p-6 flex flex-col justify-between"
            >
              {/* === PROFILE HEADER === */}
              <div className="flex items-center gap-4">
                <img
                  src={item.image}
                  alt={item.name}
                  className="w-[56px] h-[56px] rounded-full object-cover border-[3px] border-[#E5E7EB]"
                />
                <div>
                  <h3 className="font-semibold text-gray-900 text-[16px]">
                    {item.name}
                  </h3>
                  <p className="text-sm text-gray-500">{item.role}</p>
                </div>
              </div>

              {/* === TESTIMONIAL TEXT === */}
              <p className="mt-4 text-gray-600 text-[15px] leading-relaxed">
                “{item.text}”
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default TestimonialSection;
