import React from "react";
import project1 from "../assets/images/project-1.png";
import project2 from "../assets/images/project-2.png";
import project3 from "../assets/images/project-3.png";

// Data Portfolio
const portfolioItems = [
  {
    id: 1,
    title: "Landing Page Furniture",
    category: "Website Design",
    image: project1,
  },
  {
    id: 2,
    title: "Mobile App Finance",
    category: "UI/UX Design",
    image: project2,
  },
  {
    id: 3,
    title: "Branding Coffee Shop",
    category: "Branding",
    image: project3,
  },
];

function PortfolioSection() {
  return (
    <section id="portfolio" className="max-w-6xl mx-auto px-6">
      
      {/* Title */}
      <div className="text-center">
        <h2 className="text-3xl md:text-4xl font-bold text-gray-800">
          Portfolio
        </h2>
        <p className="mt-3 text-gray-600 max-w-lg mx-auto">
          Hasil karya yang pernah saya kerjakan untuk berbagai brand dan bisnis.
        </p>
      </div>

      {/* Cards */}
      <div className="mt-10 grid md:grid-cols-3 gap-6 md:gap-8">
        {portfolioItems.map((item) => (
          <div
            key={item.id}
            className="bg-white rounded-xl shadow-sm hover:shadow-md transition border border-gray-200 overflow-hidden"
          >
            {/* Thumbnail */}
            <img
              src={item.image}
              alt={item.title}
              className="w-full h-48 object-cover"
            />

            {/* Content */}
            <div className="p-5">
              <span className="text-sm text-brand-600 font-medium">
                {item.category}
              </span>
              <h3 className="text-lg font-semibold text-gray-800 mt-1">
                {item.title}
              </h3>

              <div className="flex justify-between items-center mt-4">
                <a
                  href="#"
                  className="text-sm font-medium text-brand-600 hover:underline"
                >
                  Preview â†’
                </a>

                <a
                  href="#"
                  className="text-sm font-medium text-gray-600 hover:text-gray-800"
                >
                  Detail
                </a>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default PortfolioSection;
